sections.code:
body,
button,
html,
input,
select,
textarea {
    font-family: -apple-system, BlinkMacSystemFont, 'avenir next', avenir, 'helvetica neue', helvetica, ubuntu, roboto, noto, 'segoe ui', arial, sans-serif;
}
.header-logo {
    text-decoration: none;
}
.header-logo .header-logo-default {
    display: none;
}
.header-logo::after {
    content: "Trello";
    font-family: -apple-system, BlinkMacSystemFont, 'avenir next', avenir, 'helvetica neue', helvetica, ubuntu, roboto, noto, 'segoe ui', arial, sans-serif;
    font-size: 17px;
    line-height: 30px;
    color: white;
}

.list-card {
    border-bottom: 0;
    border-radius: 0;
}

.list {
    border-radius: 0;
}
